import styled from '@emotion/styled';
import { regular, solid } from '@fortawesome/fontawesome-svg-core/import.macro';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import Image from 'next/image';
import { useState } from 'react';
import { ProjectDtoType } from 'src/redux/reducers/projects/project';
import { v4 as uuidv4 } from 'uuid';

const CardContainer = styled.div`
  margin: 10px 0;
  height: 282px;
  border: 1px solid #d4d4d4;
  border-radius: 3px;
  padding: 20px;
`;
const Head = styled.div`
  margin-bottom: 20px;
`;
const Top = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 10px;
`;

const Author = styled.span`
  font-size: ${(props) => props.theme.sizes.sm};
`;

const H3 = styled.div`
  font-size: ${(props) => props.theme.sizes.lg};
  font-weight: bold;
  overflow: hidden;
  text-overflow: ellipsis;
  display: -webkit-box;
  -webkit-line-clamp: 3;
  -webkit-box-orient: vertical;
  height: 60px;
`;
const IconWrapper = styled.div`
  display: flex;
  flex-wrap: wrap;
  flex-direction: row;

  > div {
    margin-right: 10px;

    &:last-child {
      margin-right: 0;
    }
  }
`;

const Req = styled.div`
  margin-bottom: 20px;
  span {
    margin-right: 5px;
    font-size: ${(props) => props.theme.sizes.sm};
  }
`;

const View = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: flex-end;

  > i {
    margin-right: 5px;
  }

  > span,
  > i {
    font-size: ${(props) => props.theme.sizes.sm};
  }
`;

interface SmallCardProps {
  projectDto: ProjectDtoType;
}

const SmallCard = ({ projectDto }: SmallCardProps) => {
  const {
    bookMark,
    currentPeople,
    maxPeople,
    name,
    projectSimplePositionDtoList,
    projectSimpleTechnicalStackDtoList,
    viewCount,
    register,
  } = projectDto;

  const [isBookmarked, setBookmark] = useState(bookMark); // TODO: 전역으로 빼기

  const removeDulplicatePosition = (
    duplicateList: ProjectDtoType['projectSimplePositionDtoList']
  ) => {
    if (!duplicateList) return [];

    const unique: { [id: number]: string } = {};

    duplicateList.forEach(
      ({ positionNo, positionName }) =>
        (unique[positionNo] = unique[positionNo] || positionName)
    );

    return Object.keys(unique).map((key) => ({
      positionNo: key,
      positionName: unique[+key],
    }));
  };

  return (
    <CardContainer>
      <Head>
        <Top>
          <Author>{register}</Author>
          <i onClick={() => setBookmark(!isBookmarked)}>
            {isBookmarked ? (
              <FontAwesomeIcon icon={solid('bookmark')} />
            ) : (
              <FontAwesomeIcon icon={regular('bookmark')} />
            )}
          </i>
        </Top>
        <H3>{name}</H3>
      </Head>
      <Req>
        <IconWrapper>
          {projectSimpleTechnicalStackDtoList.length !== 0 &&
            projectSimpleTechnicalStackDtoList.map((techStackDto) => (
              <div key={uuidv4()}>
                <Image
                  src={
                    techStackDto.image ||
                    'data:image/png;base64,'
                  }
                  alt={techStackDto.technicalStackName}
                  width="30px"
                  height="30px"
                  style={{
                    borderRadius: '50%',
                  }}
                />
              </div>
            ))}
        </IconWrapper>
      </Req>
      <Req>
        <span>포지션: </span>
        {removeDulplicatePosition(projectSimplePositionDtoList).map(
          (positionDto) => (
            <span key={positionDto.positionNo}>{positionDto.positionName}</span>
          )
        )}
      </Req>
      <Req>
        <span>프로젝트 인원: {currentPeople + '/' + maxPeople}</span>
      </Req>
      <View>
        <i>
          <FontAwesomeIcon icon={solid('eye')} />
        </i>
        <span>{viewCount}</span>
      </View>
    </CardContainer>
  );
};

export default SmallCard;
